<template>
  <img draggable="false" @dragstart="()=>{return false}" class="d_img" :src="src"/>
</template>

<script>
export default {
  name: 'XscImg',
  props: {
    options: Object
  },
  computed: {
    src () {
      if (this.options && this.options.src) {
        return this.options.src
      } else {
        return 'data:image/png;base64,'
      }
    }
  }
}
</script>

<style scoped>
  .d_img{
    width: 100%;
    height: 100%;
  }
</style>
